# @param {String} s
# @param {String[]} words
# @return {Integer[]}
def find_substring(s, words, indexes=[])

  return [] if s.empty? || words.length == 0

  indexes = []
  wl = words.first.length
  words_hash = {}

  words.map do |w|
    words_hash[w] =  words_hash[w].to_i + 1
  end

  (s.length - wl*words.count + 1).times do |i|
    current_word_map = {}
    words.count.times do |j|
      ss = s.slice(i+j*wl, wl)
      break unless words_hash[ss]
      current_word_map[ss] ||= 0
      current_word_map[ss] += 1

      break if current_word_map[ss] > words_hash[ss]
    end

    indexes << i if current_word_map == words_hash
  end

  indexes
end

s = "barfoofoobarthefoobarman"
words = ["bar","foo","the"]
p find_substring(s, words)

s = "aaa"
words = ["a","a"]
p find_substring(s, words)

